ClipboardMungerScriptContainer.Script:
// Take a tab+crlf delimited grid of data from clipboard.
// Prompt user for a string which acts as a filter.
// Keep only lines which have that string.

using System;
using System.Text;
using System.Windows.Forms;
using BOG.Framework;

public class Script : Interfaces.IClipboard
{
    public string Munge(string clipboardSource)
    {
		StringBuilder result = new StringBuilder();
		string Filter = string.Empty;
		if (DialogResult.OK != BOG.Framework.FormEx.InputBox ("Filter", "Enter a phrase which must exist in a line to keep it", ref Filter))
		{
			Filter = string.Empty;
		}

		foreach (string ThisLine in clipboardSource.Split(new string[] { "\r\n" }, StringSplitOptions.RemoveEmptyEntries))
		{
			if (ThisLine.IndexOf (Filter) >= 0)
			{
				result.AppendLine(ThisLine);
			}
		}
		return result.ToString();
    }
}
