ClipboardMungerScriptContainer.Script:
// SQL to CSharp string, and vice-versa

using System;
using System.Windows.Forms;

public class Script : Interfaces.IClipboard
{
        public string Munge(string clipboardSource)
        {
                string GuidFormat = "N";
                string Casing = "D";
                BOG.Framework.FormEx.InputBox ("Enter letter code for the format to use","N=32 digits (default), D=with hyphens, B=hyphens+brackets, P=hyphens+parentheses", ref GuidFormat);
                BOG.Framework.FormEx.InputBox ("Select casing, or leave blank for \"as is\"","U=All Upper, L=All Lower, all else is the default (nochange)", ref Casing);
				string YourGUID = System.Guid.NewGuid().ToString(GuidFormat);
				if (Casing.ToUpper() == "U") YourGUID = YourGUID.ToUpper();
				if (Casing.ToUpper() == "L") YourGUID = YourGUID.ToLower();
				return YourGUID;
        }
}
