ClipboardMungerScriptContainer.Script:
// SQL to CSharp string, and vice-versa

using System;
using System.Collections.Generic;
using System.Text;
using System.Windows.Forms;
using BOG.Framework;

public class Script : Interfaces.IClipboard
{
        public string Munge(string clipboardSource)
        {
                StringBuilder output = new StringBuilder();
                Dictionary<string,int> Unique = new Dictionary<string,int>();
                foreach (string s in clipboardSource.Split (new string[] { "\r\n" }, StringSplitOptions.RemoveEmptyEntries))
                {
                	string key = s.ToLower();
                    if (! Unique.ContainsKey (key))
                    {
                    	Unique.Add (key, 0);
                    }
                    Unique[key]++;
                }
                foreach (string s in Unique.Keys)
                {
                	output.AppendLine (string.Format ("{0,8:#,0}: {1}", Unique[s], s));
                }
                return output.ToString();
        }
}
